template.value:
var checkAge = (rule, value, callback) => {
  if (!value) {
    return callback(new Error('empty'));
  }
  callback();
};

this.$message({
  message: '恭喜你，这是一条成功消息',
  type: 'success'
});

this.$message({
  message: '恭喜你，这是一条成功消息',
  showClose: true,
  type: 'success'
});

this.$alert('这是一段内容', '标题名称', {
  confirmButtonText: '确定',
  callback: action => {
    this.$message({
      type: 'info',
      message: 'message'
    });
  }
});

this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
  confirmButtonText: '确定',
  cancelButtonText: '取消',
  type: 'warning'
}).then(() => {
  this.$message({
    type: 'success',
    message: '删除成功!'
  });
}).catch(() => {
  this.$message({
    type: 'info',
    message: '已取消删除'
  });          
});

this.$prompt('请输入邮箱', '提示', {
  confirmButtonText: '确定',
  cancelButtonText: '取消',
  inputPattern: //,
  inputErrorMessage: 'wrong'
}).then(({ value }) => {
  this.$message({
    type: 'success',
    message: '你的邮箱是: ' + value
  });
}).catch(() => {
  this.$message({
    type: 'info',
    message: '取消输入'
  });       
});

this.$notify({
  title: '标题名称',
  message: h('i', { style: 'color: teal'}, 'notify')
});

this.$notify({
  title: '提示',
  message: '不会自动关闭的消息',
  duration: 0
});

this.$notify({
  title: '成功',
  message: '这是一条成功的提示消息',
  type: 'success'
});

